'use strict';

exports.name = 'CtMrpConfig';

exports.setUp = (app, mongoose) =>
{
  const configSchema = new mongoose.Schema({
    date: Date,
    unbalance: Number,
    bottleneck: Number
  }, {
    _id: false,
    minimize: false
  });

  const mrpConfigSchema = new mongoose.Schema({
    _id: String,
    configs: [configSchema]
  }, {
    id: false,
    minimize: false
  });

  mrpConfigSchema.statics.TOPIC_PREFIX = 'ct.mrpConfigs';
  mrpConfigSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, mrpConfigSchema);
};
