'use strict';

exports.name = 'CtOrderGroup';

exports.setUp = (app, mongoose) =>
{
  const groupSchema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    nameInclude: [[String]],
    nameExclude: [[String]],
    nc12Include: [String],
    nc12Exclude: [String],
    bomInclude: [[String]],
    bomExclude: [[String]]
  }, {
    id: false,
    minimize: false
  });

  groupSchema.statics.TOPIC_PREFIX = 'ct.orderGroups';
  groupSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, groupSchema);
};
