'use strict';

exports.name = 'CtUpphQuarterlyConfig';

exports.setUp = (app, mongoose) =>
{
  const configSchema = new mongoose.Schema({
    mrps: [String],
    qtyCoeff: Number
  }, {
    _id: false,
    minimize: false
  });

  const upphQuarterlyConfigSchema = new mongoose.Schema({
    _id: Date,
    configs: [configSchema]
  }, {
    id: false,
    minimize: false
  });

  upphQuarterlyConfigSchema.statics.TOPIC_PREFIX = 'ct.upphQuarterlyConfigs';
  upphQuarterlyConfigSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, upphQuarterlyConfigSchema);
};
