'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    CtBalancingPce
  } = module;

  try
  {
    await CtBalancingPce.deleteOne({_id: req.params.id});
  }
  catch (err)
  {
    return next(err);
  }

  res.sendStatus(204);

  app.broker.publish(`${CtBalancingPce.TOPIC_PREFIX}.updated`, {
    deleted: [{_id: req.params.id}]
  });
};
