'use strict';

const report = require('../reports/groups');

module.exports = (app, module, req, res, next) =>
{
  const reports = app[module.config.reportsId];

  if (!reports)
  {
    return next(app.createError('Reports disabled.', 'UNAVAILABLE', 500));
  }

  const query = req.query;
  const options = {
    fromTime: reports.helpers.getTime(query.from) || null,
    toTime: reports.helpers.getTime(query.to) || null,
    minQty: parseInt(query.minQty, 10) || 0,
    maxQty: parseInt(query.maxQty, 10) || 0,
    minT: /^([0-9]{1,4}%|[0-9]{1,5})$/.test(query.minT) ? query.minT : null,
    maxT: /^([0-9]{1,4}%|[0-9]{1,5})$/.test(query.maxT) ? query.maxT : null,
    lines: (query.lines || '').split(',').filter(v => !!v)
  };

  reports.generateResponse(report, options, req, res, next);
};
