'use strict';

const _ = require('lodash');

exports.name = 'DrillingOrder';

exports.setUp = (app, mongoose) =>
{
  const componentSchema = new mongoose.Schema({
    nc12: String,
    name: String,
    qty: Number,
    unit: String
  }, {
    _id: false,
    minimize: false
  });

  const childOrderSchema = new mongoose.Schema({
    order: String,
    nc12: String,
    name: String,
    qty: Number,
    mrp: String,
    components: [componentSchema],
    deleted: {
      type: Boolean,
      default: false
    }
  }, {
    _id: false,
    minimize: false
  });

  const drillingOrderSchema = new mongoose.Schema({
    _id: String,
    status: {
      type: String,
      enum: ['new', 'started', 'partial', 'finished', 'cancelled'],
      default: 'new'
    },
    startedAt: Date,
    finishedAt: Date,
    comment: String,
    order: String,
    followups: [String],
    date: Date,
    nc12: String,
    name: String,
    qty: Number,
    qtyDone: Number,
    qtyDrill: Number,
    mrp: String,
    placement: String,
    startTime: Number,
    component: {
      nc12: String,
      name: String
    },
    painted: Boolean,
    childOrders: [childOrderSchema]
  }, {
    id: false,
    minimize: false
  });

  drillingOrderSchema.statics.TOPIC_PREFIX = 'drilling.orders';
  drillingOrderSchema.statics.BROWSE_LIMIT = 1000;

  drillingOrderSchema.index({date: -1});
  drillingOrderSchema.index({status: 1, date: -1});
  drillingOrderSchema.index({order: 1});
  drillingOrderSchema.index({'childOrders.order': 1});

  drillingOrderSchema.methods.act = function(input, done)
  {
    if (!_.isPlainObject(input))
    {
      return setImmediate(done, app.createError('Invalid input.', 'INPUT', 400));
    }

    const changes = {
      _id: this._id,
      order: this.order
    };

    const comment = input.comment;

    if (typeof comment === 'string' && comment.length)
    {
      changes.comment = comment;
    }

    switch (input.action)
    {
      case 'start':
        changes.status = 'started';
        changes.startedAt = new Date();
        changes.finishedAt = null;
        break;

      case 'finish':
      {
        if (typeof input.qtyDone !== 'number' || input.qtyDone <= 0)
        {
          input.qtyDone = this.qty;
        }

        changes.finishedAt = new Date();
        changes.qtyDone = input.qtyDone;
        changes.status = changes.qtyDone >= this.qty ? 'finished' : 'partial';
        break;
      }

      case 'continue':
        changes.status = 'started';
        changes.finishedAt = null;
        break;

      case 'reset':
        changes.status = 'new';
        changes.startedAt = null;
        changes.finishedAt = null;
        changes.qtyDone = 0;
        break;

      case 'cancel':
        changes.status = 'cancelled';
        break;

      case 'comment':
        break;

      default:
        return setImmediate(done, app.createError('Unknown action.', 'INPUT', 400));
    }

    this.set(changes).save(err => done(err, changes));
  };

  mongoose.model(exports.name, drillingOrderSchema);
};
