'use strict';

const _ = require('lodash');
const step = require('h5.step');

module.exports = (app, module, req, res, next) =>
{
  const {user, DrillingOrder, DrillingEvent} = module;

  const input = req.body;

  step(
    function()
    {
      DrillingOrder.findById(req.params.id).exec(this.next());
    },
    function(err, drillOrder)
    {
      if (err)
      {
        return this.skip(err);
      }

      if (!drillOrder)
      {
        return this.skip(app.createError('Order not found.', 'NOT_FOUND', 404));
      }

      drillOrder.act(input, this.next());
    },
    function(err, changes)
    {
      if (err)
      {
        return next(err);
      }

      if (!changes)
      {
        return res.sendStatus(204);
      }

      res.json(changes);

      app.broker.publish(`${DrillingOrder.TOPIC_PREFIX}.updated.${req.params.id}`, changes);

      const userInfo = user.createUserInfo(req.session.user, req);

      if (!req.session.user.loggedIn && input.user)
      {
        userInfo.id = input.user.id;
        userInfo.label = input.user.label;
      }

      DrillingEvent.record({
        type: input.action,
        time: new Date(),
        user: userInfo,
        order: req.params.id,
        data: _.omit(input, ['action'])
      });
    }
  );
};
