'use strict';

exports.name = 'DpCode';

exports.setUp = (app, mongoose) =>
{
  const codeSchema = new mongoose.Schema({
    _id: String,
    name: String
  }, {
    id: false,
    minimize: false
  });

  codeSchema.statics.TOPIC_PREFIX = 'dummyPaint.codes';
  codeSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, codeSchema);
};
