'use strict';

exports.name = 'DpFamily';

exports.setUp = (app, mongoose) =>
{
  const familySchema = new mongoose.Schema({
    _id: String,
    paintFamily: String
  }, {
    id: false,
    minimize: false
  });

  familySchema.statics.TOPIC_PREFIX = 'dummyPaint.families';
  familySchema.statics.BROWSE_LIMIT = 100;

  familySchema.index({paintFamily: 1});

  mongoose.model(exports.name, familySchema);
};
