'use strict';

const userInfoSchema = require('user/models/userInfoSchema');

exports.name = 'DpJob';

exports.setUp = (app, mongoose) =>
{
  const jobSchema = new mongoose.Schema({
    _id: String,
    createdBy: userInfoSchema,
    createdAt: Date,
    updatedBy: userInfoSchema,
    updatedAt: Date,
    lastSeenAt: Date,
    codes: [{
      type: String,
      ref: 'DpCode'
    }],
    orders: [{
      type: String,
      ref: 'Order'
    }],
    worker: {
      type: String,
      ref: 'DpWorker'
    },
    status: String,
    notify: [userInfoSchema]
  }, {
    id: false,
    minimize: false
  });

  jobSchema.statics.TOPIC_PREFIX = 'dummyPaint.jobs';
  jobSchema.statics.BROWSE_LIMIT = 100;

  jobSchema.index({createdAt: -1});
  jobSchema.index({status: 1, createdAt: -1});
  jobSchema.index({worker: 1, createdAt: -1});

  mongoose.model(exports.name, jobSchema);
};
