'use strict';

exports.name = 'DpPaint';

exports.setUp = (app, mongoose) =>
{
  const paintSchema = new mongoose.Schema({
    family: String,
    code: String,
    nc12: String,
    name: String
  }, {
    id: false,
    minimize: false
  });

  paintSchema.statics.TOPIC_PREFIX = 'dummyPaint.paints';
  paintSchema.statics.BROWSE_LIMIT = 100;

  paintSchema.index({family: 1});
  paintSchema.index({code: 1, family: 1}, {unique: true});
  paintSchema.index({nc12: 1});

  mongoose.model(exports.name, paintSchema);
};
