'use strict';

const userInfoSchema = require('user/models/userInfoSchema');

exports.name = 'DpWorker';

exports.setUp = (app, mongoose) =>
{
  const workerSchema = new mongoose.Schema({
    _id: String,
    name: String,
    user: userInfoSchema,
    port: Number,
    lastSeenAt: Date
  }, {
    id: false,
    minimize: false
  });

  workerSchema.statics.TOPIC_PREFIX = 'dummyPaint.workers';
  workerSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, workerSchema);
};
