'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    jobs
  } = module;

  try
  {
    const result = await jobs.cancelJob(req.params.id);

    if (result)
    {
      res.json(result);
    }
    else
    {
      res.end();
    }
  }
  catch (err)
  {
    next(err);
  }
};
