'use strict';

module.exports = (app, module, req, res) =>
{
  let url = '/#dummyPaint/orders?limit(-1337)&sort(-createdAt)';

  if (req.params.filter === 'unchanged')
  {
    url += '&changed=false';
  }

  if (req.params.job)
  {
    url += '&job=' + req.params.job;
  }

  res.redirect(url);
};
