'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    jobs
  } = module;

  try
  {
    const result = await jobs.updateJob(
      req.params.id,
      req.body.job.name,
      req.body.error,
      req.body.output,
      user.createUserInfo(req.session.user, req)
    );

    if (result)
    {
      res.json(result);
    }
    else
    {
      res.end();
    }
  }
  catch (err)
  {
    next(err);
  }
};
