'use strict';

exports.name = 'FapCategory';

exports.setUp = (app, mongoose) =>
{
  const notificationSchema = new mongoose.Schema({
    subdivisions: [{
      type: mongoose.Schema.Types.ObjectId,
      ref: 'Subdivision'
    }],
    prodFunctions: [{
      type: String,
      ref: 'ProdFunction'
    }]
  }, {
    _id: false,
    minimize: false
  });

  const categorySchema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      trim: true,
      required: true
    },
    users: [{}],
    notifications: [notificationSchema],
    etoCategory: {
      type: String,
      default: null
    }
  }, {
    id: false
  });

  categorySchema.statics.TOPIC_PREFIX = 'fap.categories';
  categorySchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, categorySchema);
};
