'use strict';

exports.name = 'GftPcb';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    code: {
      type: String,
      required: true,
      pattern: /^[0-9]{12}$/
    },
    name: {
      type: String,
      required: true,
      maxLength: 100
    },
    quantity: {
      type: Number,
      required: true,
      min: 1,
      max: 3
    },
    productFamily: [{
      type: String,
      pattern: /^(ANY|[A-Z0-9]{6})$/
    }],
    lampColor: [{
      type: Number,
      min: 0,
      max: 9999
    }],
    ledCount: [{
      type: Number,
      min: 0,
      max: 200
    }]
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'gft.pcbs';
  schema.statics.BROWSE_LIMIT = 100;

  schema.index({code: 1, quantity: 1}, {unique: true});
  schema.index({productFamily: 1});
  schema.index({lampColor: 1});
  schema.index({ledCount: 1});

  return schema;
};
