'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    express,
    GftPcb
  } = module;

  const pcb = await GftPcb.findOne({code: req.body.code, quantity: req.body.quantity}).exec();

  if (pcb)
  {
    req.model = pcb;

    express.crud.editRoute(app, GftPcb, req, res, next);
  }
  else
  {
    express.crud.addRoute(app, GftPcb, req, res, next);
  }
};
