'use strict';

exports.name = 'LucaEvent';

exports.setUp = (app, mongoose) =>
{
  const lucaEventSchema = new mongoose.Schema({
    line: {
      type: String,
      required: true
    },
    type: {
      type: String,
      required: true
    },
    time: {
      type: Date,
      required: true
    },
    station: {
      type: Number,
      default: -1
    },
    order: {
      type: String,
      default: ''
    },
    data: {}
  }, {
    id: false,
    versionKey: false,
    minimize: false
  });

  lucaEventSchema.statics.TOPIC_PREFIX = 'luca.events';
  lucaEventSchema.statics.BROWSE_LIMIT = 100;

  lucaEventSchema.index({time: -1});
  lucaEventSchema.index({order: 1, time: -1});
  lucaEventSchema.index({line: 1, time: -1}, {unique: true});
  lucaEventSchema.index({type: 1, time: -1});
  lucaEventSchema.index({station: 1, time: -1});

  mongoose.model(exports.name, lucaEventSchema);
};
