'use strict';

const step = require('h5.step');

module.exports = (app, {LucaEvent}, req, res, next) =>
{
  step(
    function()
    {
      LucaEvent.distinct('type').exec(this.group());
      LucaEvent.distinct('line').exec(this.group());
    },
    function(err, dictionaries)
    {
      if (err)
      {
        return next(err);
      }

      const result = {
        types: dictionaries.shift(),
        lines: dictionaries.shift()
      };

      res.json(result);
    }
  );
};
