'use strict';

exports.name = 'Luma2Line';

exports.setUp = (app, mongoose) =>
{
  const luma2LineSchema = new mongoose.Schema({
    _id: String,
    active: {
      type: Boolean,
      required: true
    },
    host: {
      type: String,
      required: true,
      trim: true
    },
    port: {
      type: Number,
      required: true,
      min: 1,
      max: 0xFFFF
    },
    unit: {
      type: Number,
      required: true,
      min: 0,
      max: 0xFF
    }
  }, {
    id: false
  });

  luma2LineSchema.statics.TOPIC_PREFIX = 'luma2.lines';
  luma2LineSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, luma2LineSchema);
};
