'use strict';

const mongoose = require('mongoose');
const userInfoSchema = require('user/models/userInfoSchema');

module.exports = new mongoose.Schema({
  _id: {
    type: String,
    required: true
  },
  date: {
    type: Date,
    required: true
  },
  user: userInfoSchema,
  type: {
    type: String,
    required: true
  },
  size: {
    type: Number,
    required: true
  },
  name: {
    type: String,
    required: true
  }
}, {
  id: false,
  minimize: false
});
