'use strict';

const mongoose = require('mongoose');
const userInfoSchema = require('user/models/userInfoSchema');

const KINDS = ['other', 'before', 'after'];

const schema = module.exports = new mongoose.Schema({
  _id: {
    type: String,
    required: true
  },
  date: {
    type: Date,
    required: true
  },
  user: userInfoSchema,
  type: {
    type: String,
    required: true
  },
  size: {
    type: Number,
    required: true
  },
  file: String,
  name: {
    type: String,
    required: true,
    trim: true
  },
  kind: {
    type: String,
    enum: KINDS,
    default: 'other'
  },
  meta: {
    type: {},
    default: () => ({})
  }
}, {
  id: false,
  minimize: false
});

schema.statics.KINDS = KINDS;
