'use strict';

const mongoose = require('mongoose');
const {transliterate} = require('transliteration');
const userInfoSchema = require('user/models/userInfoSchema');

const schema = module.exports = new mongoose.Schema({
  _id: {
    type: String,
    required: true
  },
  date: {
    type: Date,
    required: true
  },
  user: userInfoSchema,
  type: {
    type: String,
    required: true
  },
  size: {
    type: Number,
    required: true
  },
  file: String,
  name: {
    type: String,
    required: true,
    trim: true
  },
  meta: {
    type: {},
    default: () => ({})
  }
});

schema.pre('save', function(next)
{
  this.file = transliterate(this.name, {unknown: ''}).replace(/\s+/g, '_').replace(/_+/g, '_');

  next();
});
