'use strict';

const mongoose = require('mongoose');
const userInfoSchema = require('user/models/userInfoSchema');

module.exports = new mongoose.Schema({
  types: [{
    type: String,
    enum: ['nearMiss', 'kaizen', 'action', 'observation']
  }],
  kinds: [{
    type: Number,
    ref: 'OshKind'
  }],
  users: [userInfoSchema]
}, {
  _id: false,
  minimize: false
});
