'use strict';

const userInfoSchema = require('user/models/userInfoSchema');

exports.name = 'OshBrigade';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    date: {
      type: Date,
      required: true
    },
    shift: {
      type: Number,
      required: true,
      min: 1,
      max: 4
    },
    leader: {
      type: userInfoSchema,
      required: true
    },
    members: [userInfoSchema]
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'osh.brigades';
  schema.statics.BROWSE_LIMIT = 100;

  schema.index({date: -1});
  schema.index({'leader.id': 1, date: -1}, {unique: true});
  schema.index({'members.id': 1, date: -1});

  return schema;
};
