'use strict';

const mongoose = require('mongoose');

module.exports = new mongoose.Schema({
  _id: {
    type: Number,
    required: true
  },
  type: {
    type: String,
    enum: ['nearMiss', 'kaizen'],
    required: true
  }
}, {
  id: false,
  minimize: false
});
