'use strict';

const mongoose = require('mongoose');

module.exports = new mongoose.Schema({
  _id: {
    type: Number,
    default: 0
  },
  rid: {
    type: String,
    default: ''
  },
  type: {
    type: String,
    enum: ['unspecified', 'nearMiss', 'kaizen', 'action', 'observation'],
    default: 'unspecified'
  }
}, {
  id: false,
  minimize: false
});
