'use strict';

const mongoose = require('mongoose');
const userInfoSchema = require('user/models/userInfoSchema');

module.exports = new mongoose.Schema({
  user: userInfoSchema,
  roles: [{
    type: String,
    required: true,
    enum: ['creator', 'implementer', 'coordinator', 'participant', 'subscriber']
  }],
  lastSeenAt: Date,
  notify: Boolean,
  changes: {}
}, {
  _id: false,
  minimize: false
});
