'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshAccident
  } = module;

  const sessionUser = req.session.user;

  try
  {
    const accident = await OshAccident.findById(req.params.id).exec();

    if (!accident)
    {
      return next(app.createError('Entry not found.', 'NOT_FOUND', 404));
    }

    if (!user.isAllowedTo(sessionUser, [[`${OshAccident.PRIVILEGE_PREFIX}:MANAGE`]])
      && !accident.canDelete(sessionUser._id))
    {
      return next(app.createError('Forbidden.', 'AUTH', 403));
    }

    await accident.collection.deleteOne({_id: accident._id});

    app.broker.publish(`${OshAccident.TOPIC_PREFIX}.deleted`, {
      user: user.createUserInfo(sessionUser, req),
      model: accident
    });

    res.sendStatus(204);
  }
  catch (err)
  {
    next(err);
  }
};
