'use strict';

const prepareBrowseFilter = require('../../util/prepareBrowseFilter');
const exportRoute = require('./export');
const addRoute = require('./add');
const editRoute = require('./edit');
const deleteRoute = require('./delete');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshAccident
  } = module;

  const canView = user.auth('OSH:ACCIDENTS:VIEW');
  const canAdd = user.auth('OSH:ACCIDENTS:MANAGE');
  const canEdit = canView;
  const canDelete = canAdd;

  express.get(
    `/osh/accidents`,
    canView,
    prepareBrowseFilter(app, module),
    express.crud.browseRoute.bind(null, app, OshAccident)
  );
  express.get(
    '/osh/accidents;export.:format?',
    canView,
    prepareBrowseFilter(app, module),
    exportRoute.bind(null, app, module)
  );
  express.post(`/osh/accidents`, canAdd, addRoute.bind(null, app, module));
  express.get(`/osh/accidents/:id`, canView, express.crud.readRoute.bind(null, app, {
    model: OshAccident,
    idProperty: req => req.params.id.startsWith(OshAccident.RID_PREFIX) ? 'rid' : '_id'
  }));
  express.put(`/osh/accidents/:id`, canEdit, editRoute.bind(null, app, module));
  express.patch(`/osh/accidents/:id`, canEdit, editRoute.bind(null, app, module));
  express.delete(`/osh/accidents/:id`, canDelete, deleteRoute.bind(null, app, module));
};
