'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshAction
  } = module;

  const sessionUser = req.session.user;

  try
  {
    const action = await OshAction.findById(req.params.id).exec();

    if (!action)
    {
      return next(app.createError('Entry not found.', 'NOT_FOUND', 404));
    }

    if (!user.isAllowedTo(sessionUser, [[`${OshAction.PRIVILEGE_PREFIX}:MANAGE`]])
      && !action.canDelete(sessionUser._id))
    {
      return next(app.createError('Forbidden.', 'AUTH', 403));
    }

    await action.collection.deleteOne({_id: action._id});

    app.broker.publish(`${OshAction.TOPIC_PREFIX}.deleted`, {
      user: user.createUserInfo(sessionUser, req),
      model: action
    });

    res.sendStatus(204);
  }
  catch (err)
  {
    next(err);
  }
};
