'use strict';

const prepareBrowseFilter = require('../../util/prepareBrowseFilter');
const addRoute = require('./add');
const editRoute = require('./edit');
const deleteRoute = require('./delete');
const resolutionsRoute = require('./resolutions');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshAction
  } = module;

  const canView = user.auth('USER');
  const canAdd = canView;
  const canEdit = canView;
  const canDelete = canView;

  express.get(
    `/osh/actions`,
    canView,
    prepareBrowseFilter(app, module),
    express.crud.browseRoute.bind(null, app, OshAction)
  );
  express.post(`/osh/actions`, canAdd, addRoute.bind(null, app, module));
  express.get(`/osh/actions/:id`, canView, express.crud.readRoute.bind(null, app, {
    model: OshAction,
    idProperty: req => req.params.id.startsWith(OshAction.RID_PREFIX) ? 'rid' : '_id'
  }));
  express.get(`/osh/actions/:id/resolutions`, canView, resolutionsRoute.bind(null, app, module));
  express.put(`/osh/actions/:id`, canEdit, editRoute.bind(null, app, module));
  express.patch(`/osh/actions/:id`, canEdit, editRoute.bind(null, app, module));
  express.delete(`/osh/actions/:id`, canDelete, deleteRoute.bind(null, app, module));
};
