'use strict';

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshBrigade
  } = module;

  const canView = user.auth('USER');
  const canManage = user.auth('OSH:HR:MANAGE', 'OSH:LEADER');

  express.get(`/osh/brigades`, canView, express.crud.browseRoute.bind(null, app, OshBrigade));
  express.post(`/osh/brigades`, canManage, express.crud.addRoute.bind(null, app, OshBrigade));
  express.get(`/osh/brigades/:id`, canView, express.crud.readRoute.bind(null, app, OshBrigade));
  express.put(`/osh/brigades/:id`, canManage, express.crud.editRoute.bind(null, app, OshBrigade));
  express.delete(`/osh/brigades/:id`, canManage, express.crud.deleteRoute.bind(null, app, OshBrigade));
};
