'use strict';

const recountRoute = require('./recount');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshEmployment
  } = module;

  const canView = user.auth('OSH:HR:VIEW');
  const canManage = user.auth('OSH:HR:MANAGE');

  express.get(`/osh/employments`, canView, express.crud.browseRoute.bind(null, app, OshEmployment));
  express.post(`/osh/employments`, canManage, express.crud.addRoute.bind(null, app, OshEmployment));
  express.get(`/osh/employments/:id`, canView, express.crud.readRoute.bind(null, app, OshEmployment));
  express.put(`/osh/employments/:id`, canManage, express.crud.editRoute.bind(null, app, OshEmployment));
  express.delete(`/osh/employments/:id`, canManage, express.crud.deleteRoute.bind(null, app, OshEmployment));

  express.get(`/osh/employments;recount`, canManage, recountRoute.bind(null, app, module));
};
