'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshKaizen
  } = module;

  const sessionUser = req.session.user;

  try
  {
    const kaizen = await OshKaizen.findById(req.params.id).exec();

    if (!kaizen)
    {
      return next(app.createError('Entry not found.', 'NOT_FOUND', 404));
    }

    if (!user.isAllowedTo(sessionUser, [[`${OshKaizen.PRIVILEGE_PREFIX}:MANAGE`]])
      && !kaizen.canDelete(sessionUser._id))
    {
      return next(app.createError('Forbidden.', 'AUTH', 403));
    }

    await kaizen.collection.deleteOne({_id: kaizen._id});

    app.broker.publish(`${OshKaizen.TOPIC_PREFIX}.deleted`, {
      user: user.createUserInfo(sessionUser, req),
      model: kaizen
    });

    res.sendStatus(204);
  }
  catch (err)
  {
    next(err);
  }
};
