'use strict';

const addRoute = require('./add');
const editRoute = require('./edit');
const deleteRoute = require('./delete');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshKaizen
  } = module;

  const canView = user.auth('USER');
  const canAdd = canView;
  const canEdit = canView;
  const canDelete = canView;

  express.get(`/osh/kaizens`, canView, express.crud.browseRoute.bind(null, app, OshKaizen));
  express.post(`/osh/kaizens`, canAdd, addRoute.bind(null, app, module));
  express.get(`/osh/kaizens/:id`, canView, express.crud.readRoute.bind(null, app, {
    model: OshKaizen,
    idProperty: req => req.params.id.startsWith(OshKaizen.RID_PREFIX) ? 'rid' : '_id'
  }));
  express.put(`/osh/kaizens/:id`, canEdit, editRoute.bind(null, app, module));
  express.delete(`/osh/kaizens/:id`, canDelete, deleteRoute.bind(null, app, module));
};
