'use strict';

const _ = require('lodash');

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshNearMiss
  } = module;

  const input = _.pick(req.body, [
    'anonymous',
    'subject',
    'userWorkplace',
    'userDivision',
    'workplace',
    'division',
    'building',
    'kind',
    'location',
    'eventDate',
    'eventCategory',
    'reasonCategory',
    'problem',
    'reason',
    'solution',
    'implementer',
    'plannedAt',
    'priority',
    'attachments'
  ]);

  if (input.attachments && Array.isArray(input.attachments.added))
  {
    input.attachments = module.attachments.claimPendingUploads(input.attachments.added);
  }
  else
  {
    input.attachments = [];
  }

  try
  {
    const creator = !input.anonymous
      ? user.createUserInfo(req.session.user, req)
      : module.config.anonymousUserInfo;

    const implementer = !input.anonymous && input.implementer && input.implementer.id === creator.id
      ? creator
      : null;

    const nearMiss = new OshNearMiss({
      ...input,
      status: 'new',
      createdAt: new Date(),
      startedAt: null,
      finishedAt: null,
      duration: 0,
      creator,
      implementer
    });

    nearMiss.coordinators = await OshNearMiss.resolveCoordinators(nearMiss);

    nearMiss.updateParticipants([], creator);

    await nearMiss.save();

    app.broker.publish(`${OshNearMiss.TOPIC_PREFIX}.added`, {
      user: nearMiss.creator,
      model: nearMiss
    });

    res.json(nearMiss);
  }
  catch (err)
  {
    next(err);
  }
};
