'use strict';

const prepareBrowseFilter = require('../../util/prepareBrowseFilter');
const addRoute = require('./add');
const editRoute = require('./edit');
const deleteRoute = require('./delete');
const statsRoute = require('./stats');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshNearMiss
  } = module;

  const canView = user.auth('USER');
  const canAdd = canView;
  const canEdit = canView;
  const canDelete = canView;

  express.get(
    `/osh/nearMisses`,
    canView,
    prepareBrowseFilter(app, module),
    express.crud.browseRoute.bind(null, app, OshNearMiss)
  );
  express.post(`/osh/nearMisses`, canAdd, addRoute.bind(null, app, module));
  express.get(`/osh/nearMisses/stats`, canView, statsRoute.bind(null, app, module));
  express.get(`/osh/nearMisses/:id`, canView, express.crud.readRoute.bind(null, app, {
    model: OshNearMiss,
    idProperty: req => req.params.id.startsWith(OshNearMiss.RID_PREFIX) ? 'rid' : '_id'
  }));
  express.put(`/osh/nearMisses/:id`, canEdit, editRoute.bind(null, app, module));
  express.patch(`/osh/nearMisses/:id`, canEdit, editRoute.bind(null, app, module));
  express.delete(`/osh/nearMisses/:id`, canDelete, deleteRoute.bind(null, app, module));
};
