'use strict';

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshObservation
  } = module;

  const sessionUser = req.session.user;

  try
  {
    const observation = await OshObservation.findById(req.params.id).exec();

    if (!observation)
    {
      return next(app.createError('Entry not found.', 'NOT_FOUND', 404));
    }

    if (!user.isAllowedTo(sessionUser, [[`${OshObservation.PRIVILEGE_PREFIX}:MANAGE`]])
      && !observation.canDelete(sessionUser._id))
    {
      return next(app.createError('Forbidden.', 'AUTH', 403));
    }

    await observation.collection.deleteOne({_id: observation._id});

    app.broker.publish(`${OshObservation.TOPIC_PREFIX}.deleted`, {
      user: user.createUserInfo(sessionUser, req),
      model: observation
    });

    res.sendStatus(204);
  }
  catch (err)
  {
    next(err);
  }
};
