'use strict';

const _ = require('lodash');
const locks = require('util/locks');

module.exports = async (app, module, req, res, next) =>
{
  const {
    user,
    OshObservation
  } = module;

  const releaseLock = await locks.createAsync(`${OshObservation.TOPIC_PREFIX}.${req.params.id}`);

  const sessionUser = req.session.user;
  const updater = user.createUserInfo(sessionUser, req);
  const updatedAt = new Date();

  try
  {
    const observation = await OshObservation.findById(req.params.id).exec();

    if (!observation)
    {
      return next(app.createError('Entry not found.', 'NOT_FOUND', 404));
    }

    const canManage = user.isAllowedTo(sessionUser, [[`${OshObservation.PRIVILEGE_PREFIX}:MANAGE`]]);
    const isCreator = observation.isCreator(updater.id);
    const isCoordinator = observation.isCoordinator(updater.id);
    const allowedInput = ['comment'];

    if (canManage || isCoordinator)
    {
      allowedInput.push(
        'workplace',
        'division',
        'building',
        'location',
        'station'
      );
    }

    if (canManage || isCreator || isCoordinator)
    {
      allowedInput.push(
        'subject',
        'date',
        'company',
        'companyName',
        'behaviors',
        'workConditions',
        'attachments'
      );
    }

    const input = _.pick(req.body, allowedInput);

    module.attachments.prepareInput(input, observation);

    if (input.division && input.division !== observation.division)
    {
      input.coordinators = await OshObservation.resolveCoordinators(observation);
    }

    const usersToNotify = observation.applyChanges(input, updater, updatedAt);

    if (!usersToNotify)
    {
      return res.sendStatus(204);
    }

    await observation.save();

    app.broker.publish(`${OshObservation.TOPIC_PREFIX}.edited`, {
      user: updater,
      model: observation
    });

    app.broker.publish(`${OshObservation.TOPIC_PREFIX}.updated.${observation._id}`, {
      _id: observation._id,
      change: _.last(observation.changes),
      notify: usersToNotify
    });

    res.json(observation);
  }
  catch (err)
  {
    next(err);
  }
  finally
  {
    releaseLock();
  }
};
