'use strict';

const addRoute = require('./add');
const editRoute = require('./edit');
const deleteRoute = require('./delete');

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshObservation
  } = module;

  const canView = user.auth('USER');
  const canAdd = user.auth('OSH:OBSERVER', 'OSH:COORDINATOR', 'OSH:OBSERVATIONS:MANAGE');
  const canEdit = canView;
  const canDelete = canView;

  express.get(`/osh/observations`, canView, express.crud.browseRoute.bind(null, app, OshObservation));
  express.post(`/osh/observations`, canAdd, addRoute.bind(null, app, module));
  express.get(`/osh/observations/:id`, canView, express.crud.readRoute.bind(null, app, {
    model: OshObservation,
    idProperty: req => req.params.id.startsWith(OshObservation.RID_PREFIX) ? 'rid' : '_id'
  }));
  express.put(`/osh/observations/:id`, canEdit, editRoute.bind(null, app, module));
  express.patch(`/osh/observations/:id`, canEdit, editRoute.bind(null, app, module));
  express.delete(`/osh/observations/:id`, canDelete, deleteRoute.bind(null, app, module));
};
