'use strict';

const countReport = require('wmes-osh/reports/count');

module.exports = (app, {reports}, req, res, next) =>
{
  const options = {
    type: req.params.type,
    interval: reports.helpers.getInterval(req.query.interval, 'month'),
    conditions: req.rql.selector.args.filter(term => term.name !== 'eq' || term.args[0] !== 'interval')
  };

  reports.helpers.generateReport(
    app,
    reports,
    countReport,
    'wmes-osh/count',
    req.reportHash,
    options,
    (err, reportJson) =>
    {
      if (err)
      {
        return next(err);
      }

      res.type('json');
      res.send(reportJson);
    }
  );
};
