'use strict';

const report = require('wmes-osh/reports/rewards');

module.exports = (app, {reports}, req, res, next) =>
{
  const options = {
    conditions: req.rql.selector.args
  };

  reports.helpers.generateReport(
    app,
    reports,
    report,
    'wmes-osh/rewards',
    req.reportHash,
    options,
    (err, reportJson) =>
    {
      if (err)
      {
        return next(err);
      }

      res.type('json');
      res.send(reportJson);
    }
  );
};
