'use strict';

module.exports = (app, module) =>
{
  const {
    user,
    express,
    OshTarget
  } = module;

  const canView = user.auth('OSH:HR:VIEW');
  const canManage = user.auth('OSH:HR:MANAGE');

  express.get(`/osh/targets`, canView, express.crud.browseRoute.bind(null, app, OshTarget));
  express.post(`/osh/targets`, canManage, express.crud.addRoute.bind(null, app, OshTarget));
  express.get(`/osh/targets/:id`, canView, express.crud.readRoute.bind(null, app, OshTarget));
  express.put(`/osh/targets/:id`, canManage, express.crud.editRoute.bind(null, app, OshTarget));
  express.delete(`/osh/targets/:id`, canManage, express.crud.deleteRoute.bind(null, app, OshTarget));
};
