'use strict';

exports.name = 'ScrapComponent';

exports.setUp = (app, mongoose) =>
{
  const componentSchema = new mongoose.Schema({
    code: String,
    name: String,
    type: String,
    mvt: Number,
    qty: Number,
    uom: String,
    pln: Number,
    eur: Number,
    user: String,
    time: Date,
    text: String
  }, {
    id: false,
    versionKey: false
  });

  componentSchema.statics.TOPIC_PREFIX = 'scrap.components';

  componentSchema.index({time: 1});

  mongoose.model(exports.name, componentSchema);
};
