'use strict';

exports.name = 'ToolCalType';

exports.setUp = (app, mongoose) =>
{
  const typeSchema = new mongoose.Schema({
    _id: {
      type: String,
      maxLength: 40,
      trim: true
    },
    name: {
      type: String,
      required: true,
      maxLength: 100,
      trim: true
    },
    active: {
      type: Boolean,
      default: true
    }
  }, {
    id: false,
    minimize: false
  });

  typeSchema.statics.TOPIC_PREFIX = 'toolcal.types';

  mongoose.model(exports.name, typeSchema);
};
