'use strict';

const fs = require('fs');

module.exports = (app, module, req, res, next) =>
{
  const file = req.file;

  if (!file)
  {
    return next(app.createError('File missing.', 'INPUT', 400));
  }

  const hash = file.filename.replace(/\..*?$/, '');
  const attachment = {
    hash,
    type: file.mimetype,
    size: file.size,
    name: file.originalname
  };

  module.tmpAttachments.set(hash, {
    data: attachment,
    timer: setTimeout(removeAttachmentFile, 30 * 60 * 1000, hash, file.path)
  });

  res.json(attachment);

  function removeAttachmentFile(hash, filePath)
  {
    module.tmpAttachments.delete(hash);

    fs.unlink(filePath, (err) =>
    {
      if (err)
      {
        module.error(err, `Failed to remove an unused attachment.`, {filePath});
      }
      else
      {
        module.info(`Removed an unused attachment.`, {filePath});
      }
    });
  }
};
