'use strict';

exports.name = 'TrwCounter';

exports.setUp = (app, mongoose) =>
{
  const counterSchema = new mongoose.Schema({
    order: {
      type: String,
      ref: 'Order'
    },
    program: String,
    count: Number
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  counterSchema.statics.TOPIC_PREFIX = 'trw.counters';

  counterSchema.index({order: 1, program: 1});

  mongoose.model(exports.name, counterSchema);
};
