'use strict';

exports.name = 'TrwTest';

exports.setUp = (app, mongoose) =>
{
  const testSchema = new mongoose.Schema({
    user: {},
    startedAt: Date,
    finishedAt: Date,
    line: String,
    workstation: Number,
    order: String,
    pce: Number,
    program: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  testSchema.statics.TOPIC_PREFIX = 'trw.tests';

  testSchema.index({startedAt: -1});
  testSchema.index({line: 1, workstation: 1, startedAt: -1});
  testSchema.index({order: 1, startedAt: -1});
  testSchema.index({'program._id': 1, startedAt: -1});
  testSchema.index({'program.base._id': 1, startedAt: -1});
  testSchema.index({'program.base.tester._id': 1, startedAt: -1});

  mongoose.model(exports.name, testSchema);
};
